mapper.select:
select
`id`,`instance_id`,`path`,`api_id`,`gmt_create`,`gmt_modified`
from
gateway_api_springcloud

	 and id = #{id} 
	 and api_id = #{api.id} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

mapper.update:
update gateway_api_springcloud

	 instance_id = #{instanceId} 
	 path = #{path} 
	 api_id = #{api.id} 
	`gmt_modified`=sysdate()

where id = #{id}
