mapper.select:
select
`id`,`filter_type`,`rule`,`api_id`,`group_id`,`gmt_create`,`gmt_modified`
from gateway_filter

	 and id = #{id} 
	 and filter_type = #{filterType} 
	 and api_id = #{apiId} 
	 and group_id = #{groupId} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from gateway_filter

	 and id = #{id} 
	 and filter_type = #{filterType}

mapper.update:
update gateway_filter

	 and id = #{id} 
	 and filter_type = #{filterType} 
	 and api_id = #{apiId} 
	 and group_id = #{groupId} 
	`gmt_modified`=sysdate()

where id = #{id}
